void mainImage( out vec4 O, vec2 U ){
    O = texture(iChannel0, U/iResolution.xy);

    O = vec4(
        sqrt(O.z),
        O.z,
        O.z*O.z,
        1.
    );
}

const float pi = 3.14159;
//hash by IQ https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

mat2 rot(float t){
    return mat2(
        cos(t), sin(t),
        -sin(t), cos(t)
    );
}

void mainImage( out vec4 O, vec2 U ){

if (iFrame < 20){
    O = vec4(0);
    if (hash(uvec3(U, iDate.z)).x < 1e-2){
        O.xy = vec2(1, 0);
        O.z = 1.;
    }
} else {

    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    O = texelFetch(iChannel0, ivec2(U), 0);

    mat3 adj = mat3(0);
    int sum = 0;
    int dir = 0;
    vec2 d;
    for (int x = -1; x <= 1; x++){
        for (int y = -1; y <= 1; y++){
            vec4 n = texture(iChannel0, (vec2(U) + vec2(x, y))/r);
            adj[x][y] = n.x;
            sum += int(n.x);
            if (n.xy == -vec2(x,y)){
                dir += 1;
                if (n.xy != vec2(0)) d = n.xy;
            }
        }
    }


    if (O.z > 0.){
        O.xy *= 0.;
        O.z *= .99;
    }

    if (dir == 2){
        vec3 h = hash(uvec3(U, iFrame));
        O.xy = mix(
                vec2(1, 0)*rot( floor(h.x*8.)/8. * 2.*pi),
            d, sin(float(iFrame)/30.) * .1 + .5);

        if (abs(O.x) > abs(O.y)){
            O.x = O.x>0.?1.:-1.;
            O.y = round(O.y);
        } else {
            O.y = O.y>0.?1.:-1.;
            O.x = round(O.x);
        }

        O.z = 1.;
    } 

    // Check if a pixel is pointing to me and it is the only neighbour. 
    // If yes, then assign random cardinal direction. 
    // If too many neighbours, set to unchanging.
    // 
}}
