const vec4 WHITE = vec4(1.0, 1.0, 1.0, 1.0);
const vec4 BLACK = vec4(0.0, 0.0, 0.0, 1.0);

//Adapted from: https://gist.github.com/keijiro/24f9d505fac238c9a2982c0d6911d8e3
uint rand_seed;

uint hash()
{
    rand_seed ^= 2747636419u;
    rand_seed *= 2654435769u;
    rand_seed ^= rand_seed >> 16;
    rand_seed *= 2654435769u;
    rand_seed ^= rand_seed >> 16;
    rand_seed *= 2654435769u;
    return rand_seed;
}

float random()
{
    return float(hash()) / 4294967295.0; // 2^32-1
}

void srand(vec2 fragCoord, vec3 iResolution, int iFrame){
    rand_seed = uint(fragCoord.y*iResolution.x + fragCoord.x)+uint(iFrame)*uint(iResolution.x)*uint(iResolution.y);
}

vec4 update(in ivec2 int_pos, in vec4 fragColor) {
    int n=0;

    fragColor = texelFetch(iChannel0, int_pos , 0);
    n+= texelFetch(iChannel0, int_pos + ivec2(-1,-1),0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(-1,0), 0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(-1,1), 0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(0,-1), 0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(0,1),  0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(1,-1), 0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(1,0),  0) == WHITE? 1:0;
    n+= texelFetch(iChannel0, int_pos + ivec2(1,1),  0) == WHITE? 1:0;

    if(fragColor == WHITE) {
        if (n>3 || n<2) {
            return BLACK;
        }
    }

    if (n==3) {
       return WHITE;
    }

    return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 int_pos = ivec2(fragCoord);
    srand(fragCoord, iResolution, iFrame);

    fragColor = texelFetch(iChannel0, int_pos , 0);
    if (iFrame==0 && random()<0.5 ) {
        fragColor = WHITE;
        return;
    }

    fragColor = update(int_pos, fragColor);
}
