vec3 palette(vec3 coord) {
    vec3 a = vec3(1.015, 1.5, 1.05);
    vec3 b = vec3(1.0, 0.5, 0.7);
    vec3 c = vec3(2.0, 8.3, 1.090);
    vec3 d = vec3(0.780, 0.0, 1.0);



    return a + b * cos(6.28318 * (c * coord + d));
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 initUv = uv;



    vec3 finalColor = vec3(0.0);
    float z = 0.5 * sin(iTime);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.75) - 0.5;
        uv *= .175 + z * 1.2;  

        float d = length(uv) * exp(-length(initUv));
        vec3 col = palette(vec3(initUv, z + i * 0.1));  
        col *= .7 * abs(z);  

        d = abs(sin(d * 12.0 + iTime) / 12.0);
        d = smoothstep(0.0, 0.2, d);
        d = pow(0.0075 / d, 1.25);

        float noise = fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);

        finalColor += col * d * (1.5 * cos(iTime * .05 + 10. + i) * noise);
    }

    fragColor = vec4(finalColor, 1);
}
