float border = 0.12f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 m = iMouse / iResolution.x;
    float clickTime = 0.;
    if( m.w>0.0 ) // button click
	{
        clickTime = iTime;
    }
    vec2 uv = fragCoord/iResolution.xy;

    //float time = clamp((iTime - clickTime)/2.4, 0., 1.);
    float time = texture(iChannel1, uv).x;
    border *= time;
    // Normalized pixel coordinates (from 0 to 1)


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 col = texture(iChannel0, uv);
    // Output to screen
    fragColor = mix(col,vec4(vec3((col.x + col.y + col.z)/3.),1.), time);

    if(uv.y < border || uv.y > 1. - border)
    {
        fragColor = vec4(0);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 m = iMouse / iResolution.x;

    float col = texture(iChannel0, fragCoord).x;

    if(iTime < 1.)
    {
        col = 0.0001;
    }

    if( m.w> 0.0 ) // button click
	{
        if(col > 0.)
        {
            col = 0.;
        }
        else
        {
            col = 0.0001;
        }
    }
    if(col > 0.)
    {
        col = col + (0.5 * iTimeDelta);
    }
    col = clamp(col, 0., 1.);
    fragColor = vec4(col);
}
