#define t 5.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv*=2.;

    //uv -1 to +1, square
    vec3 col=vec3(.2+.8* (cos(iTime)*.5+.5),.2+.8* (sin(iTime)*.5+.5) ,  1.);


    float y= -1.+2.*fract(sin(1546.316*fract(sin(61.4*floor(iTime/t)))));
    float x=-1.+2.*fract(sin(8913.16*fract(sin(949.156*floor(iTime/t)))));

    x=x*iResolution.x/iResolution.y;;
    y=y*iResolution.y/iResolution.y;;
    vec2 point=vec2(x,y);



    float value=sin((1.-fract(iTime/t))*20.+100.*length(uv-point)) ;

    value=smoothstep(-1.,1.,value);

    col*=.5+.5*value*( 1.-smoothstep( 0., 1.,     length(uv-point)));

    //2
    /*
    y= -1.+2.*fract(sin(963.418*fract(sin(321.156*floor(iTime/t)))));
    x=-1.+2.*fract(sin(369.41*fract(sin(9844.156*floor(iTime/t)))));

    x=x*iResolution.x/iResolution.y;;
    y=y*iResolution.y/iResolution.y;;
    point=vec2(x,y);

    value=sin((1.-fract(iTime/t))*20.+100.*length(uv-point)) ;
    value=smoothstep(-1.,1.,value);

    col*=.5+.5*.5*value*( 1.-smoothstep( 0., 1.,     length(uv-point)));
    */

    col=pow(col,vec3(1./2.2));
    fragColor = vec4(col,1.0);
}
