void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 orgTexture = texture(iChannel0,uv).rgb;

    float fMak = 0.;


    float start = .1;
    float stepIndex = (1. - (start * 2.)) / 3.;

    vec3 tx = vec3(.0,.0,.0);
    vec2 tran = vec2(stepIndex, stepIndex);


    for(float i=1.; i<=3.; i+=1.){

        for(float j=1.; j<=3.; j+=1.){


            vec2 uvt = uv + vec2(tran.x * (i - j), tran.y * (i - j));
            vec2 pOvtFrom = vec2(start + stepIndex * (j - 1.), start + stepIndex * (i - 1.));
            vec2 pOvtTo = vec2(start + stepIndex * j, start + stepIndex * i);

            vec2 ovt = step(pOvtFrom, uvt) * step(uvt, pOvtTo);
            float fOvt = ovt.x * ovt.y;

            vec3 ft = texture(iChannel0,uvt).rgb;
            vec3 tOvt = mix(vec3(fOvt), ft, fOvt);

            fMak += fOvt;
            tx += tOvt;


        }

    }



    vec3 twrap = mix(vec3(1. - fMak), orgTexture, 1. - fMak);


    //vec3 tF = tOv1 + tOv2 + tOv3 + tOv4 + twrap;
    fragColor = vec4(tx, 1.);

}
