#define SMALL .003
#define TWO_PI 6.2831853

vec3 clockHand(vec2 uv, float t, float th, float ln) 
{
    float c = cos(t), s = sin(t);
    vec2 l = mat2(c, s, -s, c) * uv;
    float col = smoothstep(th+SMALL, th, abs(l.x));
    col *= smoothstep(-SMALL, 0., l.y);
    col *= smoothstep(ln+SMALL, ln, l.y);
    return vec3(col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    float luv = length(uv);
    float th = iDate.w * .00027777778;
    float tm = fract(th) * 60.;
    float ts = fract(tm) * 60.;

    th = floor(th) * .0833333 * TWO_PI;
    tm = floor(tm) * .0166666 * TWO_PI;
    ts = floor(ts) * .0166666 * TWO_PI;

    vec3 col = vec3(smoothstep(.015, .015+SMALL, luv));
    col *= vec3(smoothstep(.4+SMALL, .4, luv));
    col += vec3(smoothstep(.45, .45+SMALL, luv));
    col -= clockHand(uv, th, .005, .3);
    col -= clockHand(uv, tm, .0035, .35);
    col -= clockHand(uv, ts, .003, .36) * vec3(0,1,1);

    fragColor = vec4(col,1.);
}
