#define t iTime



float circle(vec2 uv, float radius){
    vec2 dist = uv-vec2(0.0);
    float res = 1.-smoothstep(radius-(radius*.1), radius+(radius*.1), dot(dist,dist)*4.);
    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    float aspect=iResolution.x/iResolution.y;
    uv.x*=aspect;

    vec3 col;// = vec3(circle(uv, .15));

    float mainCircle=circle(uv, .2)-circle(uv, .15);
    float innerCircle=circle(uv, .1)-circle(uv, .09);

    col=vec3(mainCircle);


    for(float i=0.0; i<0.93; i+=0.1)
    {
        col+=vec3(circle(uv, .1+i)-circle(uv, .09+i));
    }

    for(float i=0.53; i>0.1; i-=0.1)
    {
        //col+=vec3(circle(uv, (.1+i)*abs(fract(t)))-circle(uv, (.09+i)*abs(fract(t))));
        col+=vec3(circle(uv, .1+i)-circle(uv, .09+i));
    }

    for(float i=0.0; i<0.7; i+=0.1)
    {
        //col+=vec3(circle(uv, (.1+i)*abs(fract(t)))-circle(uv, (.09+i))*abs(fract(t)));
        col+=vec3(circle(uv, .1+i)-circle(uv, .09+i));
    }

    float a=.07;
    float b=.06;
    col+=vec3(circle(uv, .009*abs(fract(t))));
    col+=vec3(circle(uv, a*abs(fract(t)))-circle(uv, b*abs(fract(t))));


    //vec3 res=vec3(col.x-col.y-col.z, col.x*col.y*col.z, col.x+col.y+col.z);

    fragColor = vec4(col, 1.0);
}
