void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

float scale = 0.05;

float FloatTK(float offset){
    float res = sin(iTime * 5.0 + offset);
    return (res + 1.0) / 2.0;
}

bool norm(vec2 uv){
    float f = scale * FloatTK(0.0) + 0.001;
    vec2 scale = vec2(f * (iResolution.y / iResolution.x), f);
    vec2 muv = iMouse.xy/iResolution.xy - vec2(scale / 2.0);

    return uv.x > muv.x && uv.y > muv.y && uv.x < muv.x + scale.x && uv.y < muv.y + scale.y;
}

bool fnorm(vec2 uv){
    vec2 uvxy = uv;
    uvxy = vec2(1.0) - uvxy;

    return norm(uv) || norm(uvxy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb * 0.99;
    if(fnorm(uv)) col = vec3(FloatTK(0.0), FloatTK(90.0), FloatTK(180.0)) + vec3(0.4);

    fragColor = vec4(col,1.0);
}
