void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime*0.1)*6.28;
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    p.y *= 4.0;
  	p.y *= 2.0 * sin(p.x * 15.0 + t)+ 0.5 * sin(p.x * 25.0 + t*4.0 );
    p.y = pow(abs(p.y),12.0);
    float v = clamp(p.y , 0.0, 1.0);
    fragColor = vec4(v*0.37,v*0.75,v*0.4,1.0);
}
