void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pt = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 q = pt-vec2(0.,0.);
    float r0 = 0.6;
    float ccos = cos(iTime);
    float ssin = sin(iTime);
    float r = r0*cos(1000.+2000. * atan( q.y,  q.x) + iTime * 3.);
    float tmp1 = length(pt*vec2(ccos,ssin));
    float tmp2 = length(pt*vec2(ssin,ccos));
    vec3 baseCol = vec3((ccos + 1.) * 0.5 + tmp1,(ssin + 1.) * 0.5 + tmp2,length(iMouse.xy)/iResolution.xy);
    vec3 col = baseCol * smoothstep(0.+r,0.2+r,length(q))+baseCol*abs(r);

    // Output to screen
    fragColor = vec4(col,0.);
}
