#define PI 3.14159265358979323
#define I vec2(0., 1.)
#define E vec2(exp(1.), 0.)
#define hue(a) (.6 + .6*cos(2.*PI*a + vec3(0,-2.*PI/3.,2.*PI/3.)))

#define ZOOM (0.5*iResolution.y)

vec2 cmul(vec2 z1, vec2 z2)
{
    return mat2(z1, -z1.y, z1.x)*z2;
}
vec2 cdiv(vec2 z1, vec2 z2)
{
    return z1*mat2(z2, -z2.y, z2.x)/dot(z2, z2);
}

vec2 clog(vec2 z)
{
    return vec2(0.5*log(dot(z,z)), atan(z.y, z.x));
}

vec2 csin(vec2 z)
{
    return vec2(sin(z.x)*cosh(z.y), cos(z.x)*sinh(z.y));
}

vec2 ccos(vec2 z)
{
    return vec2(cos(z.x)*cosh(z.y), -sin(z.x)*sinh(z.y));
}

vec2 cexp(vec2 z, vec2 c)
{
    float argz, m, a;
    float lnz = dot(z,z);

    //if (lnz == 0.) return vec2(0., 0.);
    //else {
        lnz = 0.5*log(lnz);
        argz = atan(z.y, z.x);

        m = exp(c.x*lnz - c.y*argz);
        a = c.x*argz + c.y*lnz;

        return m*vec2(cos(a), sin(a));
    //}
}


vec3 hrgb(vec2 w)
{
    float a;
    float aa = atan(w.y, w.x);

    vec2 v = w;

    if (true) { //polar
        w.x = atan(v.y, v.x)/(PI/4.);
        w.y = length(v);
        w.y = log(1. + 100.*w.y);
    }

    vec3 c;

    a = (PI + aa)/(2.*PI);

    if (true) { //full colour
        //c.r =  0.5 - 0.5*sin(2.0*PI*a - PI/2.0 );
        //c.g = (0.5 + 0.5*sin(2.0*PI*a*1.5 - PI/2.0 )) * float(a < 0.66);
        //c.b = (0.5 + 0.5*sin(2.0*PI*a*1.5 + PI/2.0 )) * float(a > 0.33);

        c.r =  0.5 - 0.5*sin(2.0*PI*a - PI/2.0 );
        c.g = (0.5 + 0.5*sin(2.0*PI*a*2.0 - PI)); //* float(a < 0.66);
        c.b = (0.5 + 0.5*sin(2.0*PI*a*1.5 + PI/2.0 )) * float(a > 0.33);

        c -= 0.15*fract(w.y) + 1.5*fract(mod(a, 1.0/16.0));
        c -= 0.35*pow( abs(sin(1.*PI*v.x))*abs(sin(1.*PI*v.y)), 0.2);

    } else { //bw contouring 
        float c1;
        c1 = 1. - 1.0*pow(abs(sin(2.*PI*w.x))*abs(sin(2.*PI*w.y)), 0.15);
        c = vec3(c1, c1, c1);    
    }



    if (false) { //checkerboard
        a = aa;

        int b = (abs(int(2.*fract(w.x)))) 
              ^ (abs(int(2.*fract(w.y))));

        if (b == 0)
            if (abs(a) > PI/2.) 
                if (a > 0.) c = vec3(1.,0.,0.); else c = vec3(0.,1.,0.);
            else 
                if (a > 0.) c = vec3(1.,1.,1.); else c = vec3(0.,0.,1.);

        else {
            c = vec3(0.,0.,0.);
        }
    }

    return c;
}

vec2 f(vec2 z)
{
    vec2 w;

    //w = cdiv(vec2(1.,0.), z);
    w = vec2(1., 0.);

    for (float a=0.; a<2.*PI; a+=2.*PI/5.) {
        w = cdiv(w, z - 0.5*vec2(cos(a + .05*iTime), sin(a + .05*iTime)) );
    }

    w = cexp(E, w + vec2(0., iTime));

    return w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z, w, w1, w2, w3, w4;

    z = fragCoord - iResolution.xy/2.;

    if (iMouse.xy != vec2(0., 0.)) //to center at origin
        z -= iMouse.xy - iResolution.xy/2.;


    vec2 z0 = vec2(0., 0.);
    w  = f( z/ZOOM - z0);


    w1 = f( (z + vec2(-.25,-.25))/ZOOM  - z0);
    w2 = f( (z + vec2(-.25, .25))/ZOOM  - z0);
    w3 = f( (z + vec2( .25,-.25))/ZOOM  - z0);
    w4 = f( (z + vec2( .25, .25))/ZOOM  - z0);

    fragColor = vec4(0.2*(hrgb(w) +
                          hrgb(w1) + hrgb(w2) + 
                          hrgb(w3) + hrgb(w4)), 1.0);

    //fragColor = vec4(hrgb(w), 1.0);
}
