void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
  vec2 st = fragCoord.xy / iResolution.xy;
  st.y = fragCoord.y / iResolution.x ;
  st.y = (st.y)+iResolution.y / iResolution.x*.5;

  st = st * 2. - 1.;

  float d = length(abs(st) - iTime / 20. - 2.);
  float v = fract(d * 10.);
  fragColor = vec4(vec3(v * vec3(0.3098, 0.0196, 0.0196)), 1.);
}
