#define EPSILON 0.0001
#define MAX_STEPS 500
#define MIN_DIST 0.0
#define MAX_DIST 25.0

#define AMBIENT 0.05
#define EDGE_THICKNESS 0.005
#define SHADES 50.0

#define SLICE_THICKNESS 0.1

#define PLANE_COUNT 6
#define PLANES icosidodecahedron_approx


float TorusSDF(vec3 samplePoint, vec2 dimensions)
{
	return length( vec2(length(samplePoint.xz)-dimensions.x,samplePoint.y) )-dimensions.y;
}

float PlaneSDF( vec3 p, vec3 n, float h )
{
  // n must be normalized
  return abs(dot(p,n) + h) - SLICE_THICKNESS;
}

float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

vec3 randomPointOnUnitSphere(vec2 st) {
    float theta = 2.0 * 3.14159265359 * random(st); 
    float phi = acos(2.0 * random(st + vec2(10.0, 10.0)) - 1.0); 

    float sinPhi = sin(phi);
    vec3 point;
    point.x = sinPhi * cos(theta);
    point.y = sinPhi * sin(theta);
    point.z = cos(phi);

    return point;
}

vec3 rotateAroundAxis(vec3 v, vec3 n, float a) {
	return v * cos(a) + cross(n, v) * sin(a) + n * dot(n, v) * (1. - cos(a));
}


const vec3 icosidodecahedron_approx[] = vec3[](
    vec3(-0.32545395,  0.15232232,  0.93320825),
    vec3(-0.27622077,  0.52217858, -0.80686531),
    vec3( 0.13710178, -0.94826809, -0.28634025),
    vec3( 0.4701685 ,  0.48804455,  0.73535984),
    vec3( 0.98654012, -0.15679634, -0.04640579),
    vec3(-0.59515362, -0.6797727 ,  0.42860385)
);

const float PHI = 1.618033988749894; // (1. + sqrt(5.))/2.;
const vec3 icosidodecahedron[] = vec3[](
    normalize(vec3(-1,  PHI, 0)),
    normalize(vec3(-1, -PHI, 0)),    
    normalize(vec3(0, -1,  PHI)),
    normalize(vec3(0, -1, -PHI)),    
    normalize(vec3( PHI, 0, -1)),
    normalize(vec3(-PHI, 0, -1))
);

float SceneSDF(vec3 samplePoint)
{
    float d = 100000.0;
    for (int i=0; i<PLANE_COUNT; i++)
    {
        vec3 samplePoint2 = rotateAroundAxis(samplePoint, vec3(0.0, 1.0, 0.0), iTime / 5.0);
        float current_d = PlaneSDF(samplePoint2, PLANES[i], 0.0);
        d = min(d, current_d);
    }
    float sphere = length(samplePoint) - 1.3;
    d = max(sphere, -d);
    return d;
}

float March(vec3 origin, vec3 direction, float start, float stop, inout float edgeLength)
{
    float depth = start;

    for	(int i = 0; i < MAX_STEPS; i++)
    {
        float dist = SceneSDF(origin + (depth * direction)); // Grab min step
        edgeLength = min(dist, edgeLength);

        if (dist < EPSILON) // Hit
            return depth;

        if (dist > edgeLength && edgeLength <= EDGE_THICKNESS ) // Edge hit
            return 0.0;

        depth += dist; // Step

        if (depth >= stop) // Reached max
            break;
    }

    return stop;
}

vec3 RayDirection(float fov, vec2 size, vec2 fragCoord)
{
    vec2 xy = fragCoord - (size / 2.0);
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 EstimateNormal(vec3 point)
{
    return normalize(vec3(SceneSDF(vec3(point.x + EPSILON, point.y, point.z)) - SceneSDF(vec3(point.x - EPSILON, point.y, point.z)),
                          SceneSDF(vec3(point.x, point.y + EPSILON, point.z)) - SceneSDF(vec3(point.x, point.y - EPSILON, point.z)),
                          SceneSDF(vec3(point.x, point.y, point.z + EPSILON)) - SceneSDF(vec3(point.x, point.y, point.z - EPSILON))));
}

mat4 LookAt(vec3 camera, vec3 target, vec3 up)
{
    vec3 f = normalize(target - camera);
    vec3 s = cross(f, up);
    vec3 u = cross(s, f);

    return mat4(vec4(s, 0.0),
        		vec4(u, 0.0),
        		vec4(-f, 0.0),
        		vec4(0.0, 0.0, 0.0, 1));
}

vec3 ComputeLighting(vec3 point, vec3 lightDir, vec3 lightColor)
{    
    float rnd = 0.;
    for (int i=0; i<PLANE_COUNT; i++){
        vec3 samplePoint2 = rotateAroundAxis(point, vec3(0.0, 1.0, 0.0), iTime / 5.0);
        float current_d = dot(samplePoint2, PLANES[i]);
        rnd += current_d<0.? float(1<<(i*2)) : float(1<<(i*2 + 1));
    }     
    rnd *= .777;
    vec3 sCol = .5 + .45*cos(6.2831589*rnd/1. + vec3(0, 1, 2)*1.5);


    vec3 color = vec3(AMBIENT);
    float intensity = dot(EstimateNormal(point), normalize(lightDir));
    intensity = ceil(intensity * SHADES) / SHADES;
    intensity = max(intensity, AMBIENT);
    color = lightColor * intensity/2. + (intensity*.5 + .5)*sCol;;
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 viewDir = RayDirection(45.0, iResolution.xy, fragCoord);
    // vec3 origin = vec3(sin(iTime) * 9.0, (sin(iTime * 2.0) * 4.0) + 6.0, cos(iTime) * 9.0);
    vec3 origin = vec3(5.0, 5.0, 5.0);
    mat4 viewTransform = LookAt(origin, vec3(0.0), vec3(0.0, 1.0, 0.0));
    viewDir = (viewTransform * vec4(viewDir, 0.0)).xyz;

    float edgeLength = MAX_DIST;
    float dist = March(origin, viewDir, MIN_DIST, MAX_DIST, edgeLength);

    if (dist > MAX_DIST - EPSILON) // No hit
    {
        fragColor = vec4(0.9);
        return;
    }

    if (dist < EPSILON) // Edge hit
    {
        fragColor = vec4(0.0);
        return;
    }

    vec3 hitPoint = origin + (dist * viewDir);
    // vec3 lightDir = vec3(sin(iTime * 2.0) * 6.0, 4.0, sin(iTime * 1.25) * 5.0);
    vec3 lightDir = vec3(-2.0, 4.0, 5.0);
    vec3 color = vec3(1.0, 0.5, 0.3);
    color = vec3(192.0/255., 192./255., 192./255.); // silver-ish
    // color = vec3(205.0/255., 127./255., 50./255.); // bronze-ish

    color = ComputeLighting(hitPoint, lightDir, color);

    fragColor = vec4(color, 1.0);
}
