Main 
{
    vec4 f = A(U);
    vec4 c = B(U);
    vec4 n = A(U+vec2(0,1))+B(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0))+B(U+vec2(1,0));
    vec4 s = B(U-vec2(0,1))+B(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0))+B(U-vec2(1,0));
    Q = min(10.*c.w,1.)*(.6+.5*sin(2.*c.z+vec4(1,2,3,4)));
    Q = mix(Q,f.w*(.6+.5*sin(2.*f.z+vec4(1,2,3,4))),.5*f.w);
    Q.xyz *= .5+.5*normalize(vec3(e.w-w.w,n.w-s.w,1.)).zzz;

    Q.w = 1.;

}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)


// oneshade:
//https://www.shadertoy.com/view/7sKSRh
float erf(in float x) {
    x *= .8;
    //return sign(x) * sqrt(1.0 - exp(-1.239192 * x * x));
    return sign(x) * sqrt(1.0 - exp2(-1.787776 * x * x)); // likely faster version by @spalmer
}
float erfstep (float a, float b, float x) {
    return .5*(erf(b-x)-erf(a-x));
}
// Dave_Hoskins https://www.shadertoy.com/view/4djSRW
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

Main
{
    vec4 dQ = Q = vec4(0);
    for (float x = -4.; x<=4.;x++)
    for (float y = -4.; y<=4.;y++)
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        vec2 v = u+a.xy;
        float w = erfstep(-.5,.5,v.x)*
                  erfstep(-.5,.5,v.y);
        dQ.xyz += w*a.w*a.xyz;
        dQ.w   += w*a.w;
    }
    if (dQ.w>0.)
    {
        dQ.xyz/=dQ.w;
        Q = dQ;
    }
    Q.y -= 1e-3;
    Q.xy *= .95;
    vec4 c = C(U);
    Q.w -= c.x;
    if (U.x < 1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.w = 0.;
    if (iMouse.z>0.&&length(U-iMouse.xy)<10.)Q.w = .5,Q.z = sin(iTime);
    if (iFrame < 1) {Q = vec4(0);}
}

Main {

    Q = A(U);
    vec4 dQ = vec4(0);
    for (float x = -1.; x<=1.;x++)
    for (float y = -1.; y<=1.;y++)
    if(x!=0.||y!=0.)
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        vec4 b = B(U+u);
        float f = a.w*(a.w-1.5)+b.w;
        dQ.xy -= .5*f*u/dot(u,u);
    }
    Q += dQ;
}
