#define MARKER_RADIUS 50.0
#define THICCNESS 100.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0.0);


    vec2 p0 = vec2(200.0, 400.0);
    vec2 p1 = vec2(200.0, 200.0);
    vec2 p2 = iMouse.xy;

    fragColor += vec4(0.3, 0.2, 0.0, 1.0) * (clamp(MARKER_RADIUS - length(fragCoord - p0), 0.0, 5.0));
    fragColor += vec4(0.3, 0.2, 0.2, 1.0) * (clamp(MARKER_RADIUS - length(fragCoord - p1), 0.0, 5.0));
    fragColor += vec4(0.006, 0.002, 0.001, 1.0) * pow(clamp(MARKER_RADIUS - length(fragCoord - p2), 0.0, 33.0), 2.0);

    vec2 p3 = fragCoord;
    vec2 p12 = p2 - p1;
    vec2 p13 = fragCoord - p1;

    float d = dot(p12, p13) / length(p12);
    vec2 p4 = p1 + normalize(p12) * d;

    fragColor += vec4(0.0, 0.5, 0.0, 1.0) 
      * (clamp(THICCNESS - length(p4 - fragCoord), 0.0, 1.0)) 
      * (clamp(length(p12) - length(p4 - p1) , 0.0, 1.0)) 
      * (clamp(length(p12) - length(p4 - p2), 0.0, 1.0));

    fragColor = sqrt(fragColor);

}
