float circle(in vec2 p, in vec3 circle)
{
    // circle is vec3: xy is position, z is radius.

    return 1.0 - step(circle.z, length(p - circle.xy));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) /iResolution.y;

    float d1 = 0.28;
    float r = 0.2;
    float d2 = 0.8;
    vec3 red = vec3(204, 0, 0);
    vec3 background = vec3(1.0);


    // smallest red circle
    float circle1 = circle(p, vec3(0.0, 0.0, d1));

    // in one circle and not the smaller one
    float ring = circle(p, vec3(0.0, 0.0, d2)) * (1.0 - circle(p, vec3(0.0, 0.0, d1 + r)));

    vec3 color = mix(background, red, circle1 + ring);

    fragColor = vec4(color,1.0);
}
