#define PI 3.141592653589793
#define TWO_PI 6.28318530718
vec3 hsv2rgb(in vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0, 4.0, 2.0), 6.0)-3.0)-1.0, 0.0, 1.0);

    rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

    return c.z*mix(vec3(1.0), rgb, c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    float w = 1.0/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 4.;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(0.);

    float angle = atan(uv.x, uv.y);
    float d = length(uv);
    vec2 st = vec2(angle+PI, d);
    float r = TWO_PI/3.;
    st.x -= iTime;
    float t = cos(floor(0.5+st.x/r)*r-st.x)*(st.y)+(sin(iTime)-.5);
    col = mix(col, hsv2rgb(vec3(((angle-iTime)/TWO_PI+.5), d/2., 1.0)), smoothstep(0.4, 0., t));

    vec2 st2 = st * 3.;
     float t2 = sin(floor(st.x/r)*r-st.x)*(st2.y)+(sin(iTime * 1.5) * 15.-.5);
     col = mix(col, hsv2rgb(vec3(((angle-iTime)/TWO_PI+.5), d/2., 1.0)), smoothstep(0.4, 0., t2));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
