vec4 textxy(vec2 uv){
    vec3 xyz = GETR1*vu2xyz(uv);
    return TEXT0(vec2(atan(xyz.y,xyz.x)/pi/2.+.75,asin(xyz.z)/pi+.5));
}

void mainImage( out vec4 Clr, in vec2 Crd ){
    // Normalized pixel coordinates
    float sc = min(iResolution.x, iResolution.y);
    vec2 uv = (Crd*2.-iResolution.xy)/sc;

    // Output to screen
    Clr = (smoothstep(1., 1.-3./sc, length(uv))/2.+.5) * textxy(uv);
    Clr = (smoothstep(0., 3./sc, abs(length(uv)-D))/2.+.5) * textxy(uv);
    //Clr = vec4(vu2xyz(uv),1.);
}

void mainImage( out vec4 Clr, in vec2 Crd ){
    vec2 uv = Crd/iResolution.xy-0.5;
    Clr = vec4(cos(uv.x*pi*2.)*cos(uv.y*pi),
               sin(uv.x*pi*2.)*cos(uv.y*pi),
               sin(uv.y*pi),
               1.0)/2.+.5;
}

#define TEXT0(x) texture(iChannel0, vec2(x))


#define R1X int(iChannelResolution[1].x)
#define XY2ID(X) (int(X.y) * R1X + int(X.x))
#define ID2XY(I) ivec2(I - I / R1X * R1X, I / R1X)
#define GET1(I) texelFetch(iChannel1, ID2XY(I), 0)
#define GETR1 mat3(GET1(1).xyz, GET1(2).xyz, GET1(3).xyz)

const float pi = 4. * atan(1.), D=.9;

vec3 vu2xyz(vec2 uv){
    float l = length(uv), t = 2. * atan(l / D);
    return vec3(uv/l*sin(t),-cos(t));
}

// reference https://www.shadertoy.com/view/Mss3zH
#define P_NUM 4
#define TXTF1(x) texelFetch(iChannel1, ivec2(x), 0)

mat3 rotate(vec3 v1, vec3 v2){
    if(v1==v2) return mat3(1.);
    float c = dot(v1, v2);
    vec3 A = cross(v1, v2);
    mat3 B = mat3(   0, -A.z,  A.y,
                   A.z,    0, -A.x,
                  -A.y,  A.x,    0);
    A = normalize(A);
    return mat3(c) + (1.-c) * outerProduct(A, A) + B;
}

void mainImage( out vec4 Clr, in vec2 Crd ){

    int id = XY2ID(Crd);
    if( id>=P_NUM ) {Clr=vec4(0); return;}

    if( iFrame==0 && id!=0){
        Clr = vec4(mat3(1)[id-1],1);
        return;
    }

    vec4 m = iMouse;
    if( m.z<0.0 ) {Clr = TXTF1(Crd); return;}

    float sc = min(iResolution.x, iResolution.y);
    vec3 xyz = vu2xyz((m.xy*2.-iResolution.xy)/sc);
    if( id==0 ){    
        Clr = vec4(xyz,1);
        return;
    }
    if( m.w>0.0 ) {Clr = TXTF1(Crd); return;}
    mat3 MR = GETR1*rotate(GET1(0).xyz, xyz);
    Clr = vec4(MR[id-1],1);

}
