// When changing, include "&& d < 1e2" in for loop
// to avoid crash etc.

void mainImage(out vec4 o, vec2 u)
{
    vec2 v = iResolution.xy,
         d;

    ivec4 V;

    for(u = (u+u-v)/v.y; 
        ((V.x^V.y^V.z)-V.w) % 99 > V.w-99;
        v = u*d +9.*iTime)
        V = ivec4(v.x,              // dir 0
                  .5*v.x+.866*v.y,  // dir pi/3
                  .5*v.x-.866*v.y,  // dir 2pi/3
                  d += .1);

    V = abs(V) % 99;
    o = vec4(V ^ V.yzxw&V.zxyw) * d.r/4e3;
   // + .2*cos(4.*d.r);
}
