float sdf_circle(vec2 o, float r, vec2 p)
{
    vec2 d = p - o;
    return length(d) -r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv =  fragCoord / iResolution.y;
    //vec2 uv =

    float d = abs(sdf_circle(vec2(.5), .2, uv));
    vec3 col = vec3(step(d, 0.01), sin(iTime + (sin(iTime) + 1.) + uv.x * 11.), 0.);

    // Output to screen
    fragColor = vec4(col,.3);
}
