void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy *2. - 1.;
  vec3 col = vec3(0);

  for (float i = 1.; i < 4.; i++) {
    float c1 = sin(uv.x * 3. + i * 2. + iTime * 1.5) * 0.5;
    float c2 = cos(uv.x * 2.5) * 0.5 + 0.5; 
    float d = abs(c1 * c2 + uv.y); 
    float w = 0.01 + abs(uv.x) * 0.02;
    col += smoothstep(1. / iResolution.y, 0., d - w);
  }

  fragColor = vec4(col, 1.0);
}
