#define SCENE_SCALE               ( 2.0 )
#define AA_UNIT                   ( (SCENE_SCALE*4.0) / iResolution.y  )
#define SMAA(d)                   ( smoothstep(AA_UNIT, 0., d) )

/* https://www.shadertoy.com/view/DdsGDj */
#define MOUSE_OFFSET ( iMouse.z > 0. ? iMouse.xy - iResolution.xy * 0.5 : vec2(0) )
vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin, in bool mouse_drag)
{
    vec2 M = MOUSE_OFFSET * (mouse_drag == true ? 1. : 0.);
    return ((2. * (SC - M) - iResolution.xy) / iResolution.y) * scale + origin;
}

/* https://www.shadertoy.com/view/ddfGz2 */
vec3 colorize_field(float dist, vec3 color)
{    
    float F = 30., freq = F*dist, aa = 1./F, C = 0.7, amp = 0.2, width = F*0.01, //0.3
          waves_sdf = C + amp*smoothstep(8./iResolution.y, 0., (width+cos(freq)) * aa),
          exp_raise = 1. - exp(-4.*abs(dist));

    color = 1.0 - sign(dist) * color; // Change color if dist is negative. 
    color *= exp_raise;               // Attenuate color around edges.     
    color *= waves_sdf;               // Add waves.

    return mix(color, vec3(1), smoothstep(8./iResolution.y, 0., abs(dist)) );
}

float y_axisSDF(vec2 NDC) { return abs(NDC.x); }
float x_axisSDF(vec2 NDC) { return abs(NDC.y); }
float vertical_lineSDF(vec2 NDC, float x0) { return abs(NDC.x-x0); }
float horizontal_lineSDF(vec2 NDC, float y0) { return abs(NDC.y-y0); }
float cellsSDF(vec2 NDC)
{
    //NDC *= 2.; // Finer cells
    NDC = fract(NDC);
    NDC = fract(min(NDC, 1. - NDC));
    return
        min(vertical_lineSDF(NDC, 0.), horizontal_lineSDF(NDC, 0.));
}

/* Slightly changed original code. */
float cross2d(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; }
// Find vector ð‘£ð‘– given pixel ð‘=(0,0) and BÃ©zier points ð‘0,ð‘1,ð‘2.
vec2 get_distance_vector(vec2 NDC, vec2 b0, vec2 b1, vec2 b2) 
{
    b0 -= NDC; b1 -= NDC; b2 -= NDC;

    float a=cross2d(b0,b2), b=2.*cross2d(b1,b0), d=2.*cross2d(b2,b1); // ð›¼,ð›½,ð›¿(ð‘)
    float f=b*d-a*a; // ð‘“(ð‘)
    vec2 d21=b2-b1, d10=b1-b0, d20=b2-b0;

    vec2 gf=2.*(b*d21+d*d10+a*d20);
         gf=vec2(gf.y,-gf.x); // âˆ‡ð‘“(ð‘)
    vec2 pp=-f*gf/dot(gf,gf); // ð‘â€²
    vec2 d0p=b0-pp; // ð‘â€² to origin

    float ap=cross2d(d0p,d20), bp=2.*cross2d(d10,d0p); // ð›¼,ð›½(ð‘â€²)

    // (note that 2*ap+bp+dp=2*a+b+d=4*area(b0,b1,b2))
    float t=clamp((ap+bp)/(2.*a+b+d), 0.,1.); // ð‘¡Ì…

    return mix(mix(b0,b1,t),mix(b1,b2,t),t); // ð‘£ð‘– = ð‘(ð‘¡Ì…)
}
/* */

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = map_to_centered_ndc(SC, SCENE_SCALE, vec2(0), false),
         MP = iMouse.xy == vec2(0) ? vec2(0) :
             map_to_centered_ndc(iMouse.xy, SCENE_SCALE, vec2(0), false);

    vec3 color = vec3(1);
         color = mix( color, vec3(0.8), SMAA(cellsSDF(NDC)) );
         color = mix( color, vec3(0), SMAA(x_axisSDF(NDC)) );
         color = mix( color, vec3(0), SMAA(y_axisSDF(NDC)) );

    float offs = sinOSC(-0.5, 0.5, iTime * 0.5);
    vec2 b0 = vec2(-2.0, 0.0) * 0.65; 
    vec2 b1 = vec2(-2.0, 0.0);
    vec2 b2 = vec2(2.0, 0.0) * 0.65;

    b0 = rotateAroundZ(b0, iTime * 0.1) + vec2(2.*offs, -offs);
    b1 = rotateAroundZ(b1, iTime * 0.9) + vec2(-6.*offs, 0.0);
    b2 = rotateAroundZ(b2, iTime * 0.1) + vec2(-offs, offs);

    vec2 vi = get_distance_vector(NDC, b0, b1, b2);
    float d = length(vi) - 0.1;

    if (iMouse.z > 0.)
        color = mix(color, vec3(1,0,0), SMAA(d));        
    else
        color = colorize_field(d, vec3(.1, .4, .7));


    O = vec4(color,1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}
