void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float TempX = (fragCoord.x / iResolution.x) * 8.;
    float TempY = (fragCoord.y / iResolution.y) * 9.;
    float cx = TempX + sin(iTime) * .4;
    float cy = TempY + cos(iTime) * 4.;

    float Value = sin(TempY + iTime) + sin(TempX + iTime) + sin(TempX + TempY + iTime) + sin(sqrt(cx * cx + cy * cy ));

    fragColor = vec4(sin(Value), sin(Value + 2.5), sin(Value + 4.), 1.);
}
