vec3 SineLine(vec2 st, float t, vec3 c, float os, float pe, float amp)
{
   //Vary the length of the wave with time (between 0.5 and 0.7), 
   //Change it at different times for each color
   float wl = 0.60 + 0.1*sin(pe + iTime/2.0);

   //Move the wave along the x axiz with time
   st.x += 5.0*sin(t);

   //Calculate the distance to the center of the wave using parameters for
   //amp = amplitude, pe = period, os = offset(for y-position)
   float dist = abs(amp*sin(pe + wl*st.x) - st.y + os);

   //Create a fuzzy, blurred wave (the glow)
   float fuzzyLine = smoothstep(0.7, 0.2, dist);
   //Create a more solid line in the center
   float solidLine = smoothstep(0.26, 0.22, dist);

   //Add the lines
   float s = 0.20*solidLine + fuzzyLine;

   //Return the color combined with the line intensity
   return c * s ;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Scale -2.5 to 2.5 for x and y
    uv = (0.5-uv)*5.0; 

    //Slower time
    float t = iTime*0.5;

    //Create 4 lines of different colors and with diffent settings
    vec3 blue = SineLine(uv, t*1.5, vec3(0.1, 0.1, 0.9), -0.3, 0.3, 1.4); //Blue
    vec3 yellow = SineLine(uv, -t*1.7, vec3(0.9, 0.8, 0.1), 0.2, 1.7, 1.5); //Yellow
    vec3 red = SineLine(uv, t*1.7, vec3(0.9, 0.2, 0.1), -0.5, 3.3, 1.7);  //Red
    vec3 green = SineLine(uv, -t*1.5, vec3(0.1, 0.8, 0.2), 0.5, 4.9, 1.6); //Green

    //Mix in the lines to avoid creating too bright intersections
    vec3 col = vec3(0);
    col = blue;

    col = mix(col, yellow, yellow);
    col = mix(col, red, red);
    col = mix(col, green, green);

    //col = blue + yellow + red + green;
    // Output to screen
    fragColor = vec4(col,1.0);
}
