void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.yy;
    uv *= 2.0;

    float dist = pow(length(uv), 3.0);
    if (dist > 1.0) {
        dist = 0.0;
    }

    // fresnel border
    vec3 shield_col = vec3(0, 0.6, 1);
    vec3 fresnel = mix(vec3(0), shield_col, dist*2.0);
    fresnel = mix(fresnel, vec3(1), smoothstep(0.8, 1.0, dist));


    float d=length(uv);
    float z = sqrt(1.0 - d * d);
    float r = atan(d, z) / 3.14159;
    float phi = atan(uv.y, uv.x);
    uv = vec2(r*cos(phi)+.5,r*sin(phi)+.5);

    vec3 tex = texture(iChannel0, uv + vec2(iTime*0.1, 0)).rgb;

    float val = 1.0-dot(tex, vec3(0.299, 0.587, 0.114));
    float plate = smoothstep(0.6, 0.6, val);
    //val += fresnel;



    vec3 finalcol = max(vec3(plate*1.0) * fresnel, vec3(fresnel*1.4) * val);

    fragColor = vec4(finalcol.rgb, 1);
}
