//No02_WhiteRectMouse
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec3 col = vec3(0);
  float mx = iMouse.x > 1.0 ? iMouse.x : iResolution.x / 2.0;
  float my = iMouse.y > 1.0 ? iMouse.y : iResolution.y / 2.0;
  if ((fragCoord.x > mx) && (fragCoord.y > my)){col=vec3(1);}
  fragColor = vec4(col,1.0);
}
