#define vec4sqrt(v) vec4(sqrt((v).x), sqrt((v).y), sqrt((v).z), sqrt((v).w))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);
    fragColor = textureLod(iChannel0, uv, 2.0);
    //fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0); 

    //fragColor = 0.999 * fragColor + 0.001 * texture(iChannel1, uv);

    float brush_size = 15.0*sin(iTime*4.0) + 25.0;

    if(iMouse.z > 0.0 && length(fragCoord - iMouse.xy) < brush_size) {
        float r = pow(sin(iTime*0.8), 2.0);
        float g = pow(cos(iTime*0.9763), 2.0);
        float b = pow(cos(0.5 + iTime*1.7538), 2.0);
        fragColor = vec4(r, g, b, 1.0);

        //fragColor *= vec4sqrt(vec4sqrt(texture(iChannel1, uv)));
    }
}
