// MIT License

const float PI = 3.141592;
const float NumTiles = 10.0;

vec2 rotateAxisY(vec2 uv, float angle) {
    uv -= 0.5;
    float r = radians(angle);
    vec3 ret = inverse(mat3(cos(r),0,-sin(r), 0,1,0, 0,0,2)) * vec3(uv, 2);
    return ret.xy / ret.z + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= NumTiles;
    vec2 id = floor(uv) / NumTiles;
    vec2 localUv = fract(uv);
    uv.x /= NumTiles;

    float progress = mod(iTime*0.3, 1.0);
    float angle = clamp(progress - id.x*0.5, 0.0, 1.0) * 360.0;
    angle = clamp(angle, 0.0, 180.0);
    uv = rotateAxisY(localUv, angle);
    float flip = 90.0 < angle && angle < 270.0 ? 1.0 : 0.0;
    if (flip > 0.0) {
        uv.x = 1.0 - uv.x;
    }

    vec3 col = vec3(0.0);
    if (0.0 <= uv.x && uv.x <= 1.0 && 0.0 <= uv.y && uv.y <= 1.0) {
        uv.x = uv.x / NumTiles + id.x;
        col = mix(texture(iChannel1, uv).rgb, texture(iChannel2, uv).rgb, flip);
    }

    fragColor = vec4(col,1.0);
}
