void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=2.,e; vec3 p;
o=vec4(0.0);

for (i=0.;i++<256.&&d>1e-2;){
    p=(vec3((FC.xy-.5*iResolution.xy)/iResolution.x, 1)*e);
    p.y+=4.+iTime*2.;p.xz-=2.+iTime;

    p=(abs(abs(sin(p)-.95)-.2));
    d=min(p.x,min(p.y,p.z));
    d-=.2*min(d, dot(p,p)-3.);

e+=d;
}o+=vec4(p.zyx/.1,1)/i;
}
