float FPS = 10.f;
ivec2 AtlasSize = ivec2(6, 1);
ivec2 FrameSize = ivec2(40.f, 32.f);
ivec2 AtlasOffset = ivec2(-1, 0);

vec4 GetTextureFromAtlas(vec2 uv, int number) {
    int frameNum = number % (AtlasSize.x * AtlasSize.y);
    ivec2 currentFramePosition = ivec2(frameNum % AtlasSize.x, frameNum / AtlasSize.x);

    return texture(iChannel0, uv/vec2(AtlasSize) + vec2(currentFramePosition*FrameSize + AtlasOffset)/iChannelResolution[0].xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int timeUnit = int(floor(iTime*FPS));

    fragColor = GetTextureFromAtlas(uv, timeUnit);
}
