// Fork of "n-gon" by MonterMan. https://shadertoy.com/view/MlGyRK
// 2023-09-15 21:10:00

#define N 5
#define pi 3.1415926

#define TWEAK1 iMouse.x/iResolution.x
#define TWEAK2 iMouse.y/iResolution.y
#define TWEAK3 -iMouse.z/iResolution.x
#define TWEAK4 -iMouse.w/iResolution.y

float sdBlade(vec2 p)
{
    p -= vec2(0.35, 0.7);
    float k = exp(4.0*p.y);
    float dist = (p.x + k) / sqrt(1.0 + 16.0*k*k); // DE technique from iq
    dist = max(dist, -p.x-0.5);
    dist = max(dist, -p.y-1.4);
    return dist;
}

float map(vec2 p)
{
    float dist = sdBlade(p);
    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float d = map(uv);

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    fragColor = vec4(col,1.0);
}
