//Circle
float sdfCircle(vec2 uv, float r, vec2 offset) {
    return length(uv-offset) - r;
}

//return <x,y> from polar coordinates
vec2 polarCoord(float r, float theta){
    return vec2(sin(theta)*r,cos(theta)*r);
}

//Main Function
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

  const int AmountOfSpheres = 12;

  const float PI = 3.1415296;

  float col = 0.0;

  for(int i = 0; i< AmountOfSpheres; i++){
      vec2 circlePosition = polarCoord(0.2,iTime*2.0 + PI*float(2*i)/float(AmountOfSpheres));

      col+=smoothstep(0.0,0.01,-sdfCircle(uv,0.04,circlePosition))*(float(i/3)*0.3+0.1);
  }

  fragColor = vec4(col, col, col, 1.0);

}
