vec3 palette( float t ) {
 vec3 a = vec3(0.438, 0.407, 0.918);
vec3 b = vec3(0.781, 0.473, 0.800);
vec3 c = vec3(0.966, 1.127, 1.458); 
vec3 d = vec3(4.639, 2.462, 2.056);

return a + b*cos( 6.28318*(c*t*d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;

vec2 xy = fragCoord.xy / iResolution.xy;//Соединяем это в одну строку
    vec4 texColor = texture(iChannel0,xy);

vec3 m3=vec3(texColor.r,texColor.b,texColor.g);

vec2 uv0 = uv;

vec3 finalcolor = vec3(0.0);

for (float i = 0.0; i<2.0; i++) {
uv *=0.5;
uv=fract(uv);
uv-=0.5;

float d = length (uv)*exp(-length(uv0));

vec3 col = palette(length(uv0));

d=sin(d*8. + iTime)/8.;
d=abs(d);

d=0.02/d;

finalcolor += col*d+m3;

}


fragColor = vec4(finalcolor,1.0);
}
