void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float kernel[9];
    kernel[0] = -1.0;
    kernel[1] = -1.0;
    kernel[2] = 0.0;
    kernel[3] = -1.0;
    kernel[4] = 0.0;
    kernel[5] = 1.0;
    kernel[6] = 0.0;
    kernel[7] = 1.0;
    kernel[8] = 1.0;

    float u_kernelWeight = 1.0;
    vec2 onePixel = vec2(1.0, 1.0) / iChannelResolution[0].xy;

    vec4 color = texture( iChannel0, uv );

    vec4 colorSum =
     texture(iChannel0, uv + onePixel * vec2(-1, -1)) * kernel[0] +
     texture(iChannel0, uv + onePixel * vec2( 0, -1)) * kernel[1] +
     texture(iChannel0, uv + onePixel * vec2( 1, -1)) * kernel[2] +
     texture(iChannel0, uv + onePixel * vec2(-1,  0)) * kernel[3] +
     texture(iChannel0, uv + onePixel * vec2( 0,  0)) * kernel[4] +
     texture(iChannel0, uv + onePixel * vec2( 1,  0)) * kernel[5] +
     texture(iChannel0, uv + onePixel * vec2(-1,  1)) * kernel[6] +
     texture(iChannel0, uv + onePixel * vec2( 0,  1)) * kernel[7] +
     texture(iChannel0, uv + onePixel * vec2( 1,  1)) * kernel[8] ;

    fragColor = vec4((colorSum / u_kernelWeight + 0.5).rgb, 1.0);

}
