#define I iResolution
#define A( r ) mat2(cos(r), sin(r), -sin(r), cos(r) )
#define T iTime * .5

float df( vec3 p ){
    p.xy *= A( T );
    p.z += fract( T / 2. ) * ( 3.1415926 * 5. ) + 3.1415926 * 20.;
    p.xy *= A( p.z * sin(atan(p.x,p.y) * 20.) );

    return length( sin(abs( p ) - .5) ) - .35;
}

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = (V * 2.0 - I.xy) / I.y;

    vec3 c, p, o = vec3( 0., 0., -5. ), r = vec3( u * .2, 1. );
    float t, d, i;
    for( ; i < 64.; i++ )
        p = o + r * t,
        d = df( p ),
        t += d;

    if( d < 1e-3 )
        c += step( mod( (p.xy * A(T)).x, .125 ) - .075, 0. );

    // Output to screen
    U = vec4(c,1.0);
}
