float sandsin(vec2 uv, float amp, float freq, float height, float r)
{
    return step(float(uv.y > (amp * sin(uv.x * freq + iTime * r) + height)), 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 0.02;
    vec2 uv = fragCoord / iResolution.y; 

    float grad = uv.y * sin(iTime * 0.1) + 0.5;
    vec3 col = vec3(0.2, 0.2, 1.0) * grad;

    float sand = sandsin(uv, 0.04, 20.0, 0.3, 0.6);
    float sand2 = sandsin(uv, 0.04, 15.0, 0.2, 0.8);
    float sand3 = sandsin(uv, 0.04, 10.0, 0.1, 1.0);
    float cloud = sandsin(uv, 0.1, 5.0, 0.7, -1.0);
    float cloud2 = sandsin(uv, 0.2, 4.0, 1.0, -1.2);

    vec2 sunpos = vec2((t - floor(t)) * 2.4 - 0.2, 0.8);
    float sundist = length(uv - sunpos);
    float sun = 1.0 - smoothstep(0.1, 0.1, sundist);
    float sunhalo = 1.0 - smoothstep(0.1, 0.2, sundist);

    col = mix(col, vec3(0.7, 0.7, 0.2) * grad, sand);
    col = mix(col, vec3(0.9, 0.9, 0.2) * grad, sand2);
    col = mix(col, vec3(1.0, 1.0, 0.6) * grad, sand3);
    col = mix(col, vec3(0.5, 0.5, 0.1), 0.25 - cloud * 0.25);
    col = mix(col, vec3(1.0, 0.6, 0.3), sunhalo);
    col = mix(col, vec3(1.0, 0.4, 0.1), sun);
    col = mix(col, vec3(1.), 0.3 - cloud2 * 0.3);

    fragColor = vec4(col, 1.0);
}
