#define POINTSIZE 0.01
#define draw_point(q) o *= smoothstep(0.0, POINTSIZE, abs(q))
#define draw_circle(r, I) draw_point(length(I) - r)
#define draw_arc(t, I) draw_point(draw_arc_helper(t, I))

float inverse_radius(float t)
{
    return (2.0*t)/(t*t+1.0);
}

float arc(float t, float x)
{
    float s = inverse_radius(t);
    if(x<=-1.0) return -t;
    if(x>=+1.0) return +t;
    if(x > 0.0) return +(sqrt(1.0-(pow(s*(x-1.0),2.0)))-1.0)/s+t;
    if(x < 0.0) return -(sqrt(1.0-(pow(s*(x+1.0),2.0)))-1.0)/s-t;
    return 0.0;
}

float arc_derivative(float t, float x)
{
    float s = inverse_radius(t);
    if(x<=-1.0) return 0.0;
    if(x>=+1.0) return 0.0;
    if(x > 0.0) return -(s*(-1.0+x))/sqrt(1.0-s*s*pow(-1.0+x,2.0));
    if(x < 0.0) return +(s*(x+1.0))/sqrt(1.0-s*s*pow(x+1.0,2.0));
    return 0.0;
}

float draw_arc_helper(float t, vec2 I)
{
    return (arc(t, I.x)-I.y)/length(vec2(arc_derivative(t, I.x),-1.0)); // divide by length of the gradient to get a constant width line
}

void mainImage(out vec4 o, vec2 I)
{
    vec2 R = iResolution.xy;
    I = (I+I-R)/R.y;

    o = vec4(1);

    //draw_arc(1.0/1.0, I); // t = 1/1, s = 5/5
    //draw_arc(1.0/2.0, I); // t = 1/2, s = 4/5
    //draw_arc(1.0/3.0, I); // t = 1/3, s = 3/5
    //draw_arc(1.0/4.0, I); // t = 1/4, s = 8/17
    float t = cos(iTime*0.5);
    draw_arc(t, I);
    float s = inverse_radius(t);
    draw_circle(0.01, I-vec2(1,t-1.0/s));
    draw_circle(0.01, I-vec2(-1,-t+1.0/s));
}
