float sdBox( in vec2 p, in float b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.x;

    float rot = -0.05*iTime;
    mat2 rotation_matrix = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
   	uv = rotation_matrix * uv;
    uv += 0.5/20.0;
    vec2 scaled_uv = 20.0 * uv;

    vec2 tile = fract(scaled_uv)-0.5;
    float square_dist = length(floor(scaled_uv));
    float square_dist_distortion = square_dist*10.*(1.0+(pow(sin(iTime*0.05),2.0)));

    float edge = sin(0.5*iTime + square_dist_distortion);
    edge *= edge;
    edge = pow(edge, 2.2) * 0.5;

    float border = 0.001;

    float value = 1.5-smoothstep(0.0-border,0.0+border,sdBox(tile,edge));

    value -= square_dist*.05;
    value = max(value,0.0);
    fragColor = vec4(pow(value, 0.5), pow(value, 1.5), pow(value, 3.0), 1.);
}
