void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 bg = 0.5 + 0.3*cos(iTime+uv.xyx+vec3(0,2,5));
    vec2 mousePos = iMouse.xy;
    if (mousePos == vec2(0,0))
        mousePos = iResolution.xy / 2.0;
    mousePos += vec2(sin(iTime) * 100.0, cos(iTime) * 100.0);
    float dist = length(mousePos - fragCoord);
    fragCoord *= dist * 0.001;
    //dist = step(10.0, dist);
    dist = smoothstep(5.0, 300.0, dist);
    dist = 1.0 - dist;
    vec3 fg = vec3(sin(fragCoord.x * 0.2), cos(fragCoord.y * 0.2), 0.5);
    vec3 col = mix(bg, fg, dist);
    fragColor = vec4(col, 1);
}
