vec3 seigaiha(in vec2 uv, in float scale){

    uv = vec2(mod(uv.x, scale*2.), mod(uv.y, scale));
    uv *= 1./scale;

    bool grid = false;
    if(grid){
        if(mod(uv.y,1.) <0.02 && mod(uv.x,.2)< .1) return vec3(1.,0.,0.);
        if(mod(uv.x,1.) <0.02 && mod(uv.y,.2)< .1) return vec3(1.,0.,0.);

    }

    float radius = 1.;
    float nrings = 5.;
    float rings = radius/nrings;
    float ringswidth = .666/nrings;

    // bottom circle
    vec2 bottom = vec2(1.,-.5);
    if(distance(uv,bottom)<radius){
    vec3 col = vec3(ringswidth>(mod(distance(uv,bottom),rings)));
    return col;
    }

    // left circle
    vec2 left = vec2(0.,0.);
    if(distance(uv,left)<radius){
    vec3 col = vec3(ringswidth>(mod(distance(uv,left),rings)));
    return col;
    }

    // right circle
    vec2 right = vec2(2.,0.);
    if(distance(uv,right)<radius){
    vec3 col = vec3(ringswidth>(mod(distance(uv,right),rings)));
    return col;
    }

    // top circle
    vec2 top = vec2(1.,.5);
    if(distance(uv,top)<radius){
    vec3 col = vec3(ringswidth>(mod(distance(uv,top),rings)));
    return col;
    }

    return vec3(1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    vec3 col = seigaiha(uv, .3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
