void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Extract alpha
    vec4 foreground = texture(iChannel1, uv).rrrr;
    // Rotating color
    foreground *= vec4(sin(vec3(0.,2.,4.)+iTime),1.);

    vec4 background = texture(iChannel0, uv);

    background *= 1. - foreground.a;
    //foreground *= vec4(foreground.aaa,1.); //Premultiplied already
    // Output to screen
    fragColor = background+foreground;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int xPos = int(fragCoord.x);
    int yPos = int(fragCoord.y);

    float color = 0.;
    if((xPos & 16) > 1) {
        color = 1. - color;
    }
    if((yPos & 16) > 1) {
        color = 1. - color;
    }
    color = .3 * color + .7;
    fragColor = vec4(vec3(color),1.);
}
