// Golfing 360 chars https://shadertoy.com/view/lst3R7
// golfed variant from jt's https://www.shadertoy.com/view/4st3R7

//  -5 by Fab

void mainImage( out vec4 O,  vec2 U ) {
    vec2  I = ceil( U /= .1*iResolution.y ), 
          s = mod(I+I.y,2.) - .5,                     // checkboard flip (for coloring) 
          w = .2 + 1.6*abs(fract(iTime/16.)-.5) +U-U; // 1/w in [1/5 .. 1]
    texture(iChannel0, I/128. + iTime/1e3 ).x < .5 ? U.x *= -1., s=-s : s; // random flip (for orientation and coloring)

    O = pow( fract(vec4(U,-U)), 1./w.xxxx );
    U = smoothstep(.45,.55, pow(O.xz+O.yw, w) );
//      step(.05,          abs( () - .5 ) )        

    O = (.5+s - 2.*s * U * U.y ).xxxx;
}





/** // -3 by iq -3 by Fab

void mainImage( out vec4 O,  vec2 U ) {
    vec2  I = ceil( U /= .1*iResolution.y ), w,
