/** //  292 chars

void mainImage( out vec4 O,  vec2 U ) {
    vec2  I = ceil( U /= .1*iResolution.y ), w;
    float t = iTime,
    s = sign( texture(iChannel0, I/128. + t/1e3 ).x - .5 ); U.x *= s; // random flip (for orientation and coloring)
 // U.x *= s = expr   is bugged on OpenGL/Chrome/Nvidia
    s *= 2.* mod(I.x+I.y,2.) - 1.;                 // checkboard flip (for coloring)

    w = .2 + 1.6*abs(fract(t/16.)-.5) +U-U;        // 1/w in [1/5 .. 1]
    O = pow( fract(vec4(U,-U)), 1./w.xxxx );
    U = smoothstep(.45,.55, pow(O.xz+O.yw, w) );
//      step(.05,          abs( () - .5 ) )        

    O = .5+.5*s - s * U.xxxx * U.y;
}

