float calcAngle(vec2 uv) {
    const float pi = 3.14159265;
    vec2 zero = vec2(0.0, 1.0);
    float angle;
    if(uv == abs(uv) || uv == -abs(uv)) {
        angle = acos(abs(dot(uv, zero))/length(uv));
        if(uv.x < 0.0) {
            angle += pi;
        }
    } else {
        angle = asin(abs(dot(uv, zero))/length(uv));
        if(uv.x < 0.0) {
            angle += pi * 1.5;
        } else {
            angle += pi/2.0;
        }
    }

    angle /= pi * 2.0;
    return angle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int size = 3;
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float angle = calcAngle(uv);
    float rad = length(uv) * float(size);
    float timer = fract(float(iFrame)/100.0);
    for(int i = 0; i < size * 3; i++) {
        if(abs(rad + (timer - angle) - float(i)) < 0.05) {
            fragColor = vec4(1., 0., 1., 1.);
        }
    } 
}
