#define t iTime
#define r iResolution.xy
precision highp float;

vec3 p( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.3, 0.7, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

float par (float x, float k) {
    return pow(4.0*x*(1.0-x), k);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-r)/r.x,

         uvX= exp(uv),
         uv0= uv;     
    float ra=(0.5*sin(t*0.1)+0.9*20.)*abs(sin(t*0.1))+(70.*(0.1*sin(t*0.3)+0.6)),
          d, 
          ux=exp(length(uv)), 
          uvc=exp(-length(uv0)), 
          un=length(sin(uv*ra*(0.1*sin(t*0.3)+0.3))), 
          uu;                   
    vec3 fc, c;

    for (float i;i<3.;i++){

    uv = (fract(uv*1.2)-0.5)*(0.2*un+0.3)*(abs(sin(t*0.3))+0.3);


    uu = 0.2*sin(length(-uv)*8.)+0.5;


    ux = mod(ux*(1.2*(0.1*sin(t*0.03)+0.1*d)),1.1)+0.155;
    d = length(uv) * uvc - ux;

    //creates ugly circle, need something to fix that



    c = p(length(ux)+t*0.3);
    d = sin(d*10.+t)/8.;
    d = abs(d);
    d = 0.007*(uvc*abs(sin(t*0.5))+0.5) / d;

    d *= par(d, 0.7);
    fc += (c * d);


    }
    // Output to screen
    fragColor = vec4(fc/uu,t);
}
