#define euler(U) pow( .01, abs(sin(U).x*sin(U).y)) / pow(.98,length(U))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy,
         U = (M+u)/R - 1.;

    M = M/R - .5; // mousepos

    vec3 D = vec3(1,U.yx); 

    O = vec4 (euler( M+D.xy/D.z/.1 ) );
}
