#define r mat2(cos(.05*a + vec4(0,33,11,0))), p

void mainImage( out vec4 O, vec2 u ) {
    float i, t, k = iTime, 
                d = k, 
                a = k*3.;
    vec3  f,    p = iResolution, 
                U = vec3((u + u - p.xy) * r.y);

    for ( f++, f.z = .2; i++ < 6e1 && d > t/2e2;
        p = t * U / length(U),
        p.zy *= r.xz *= r.z += k*.5,
        O = .7 * (.8 + cos(length(floor(p*5.))/3. + k + .7*vec4(0,1,2,0)))/exp(t/7.)            
    )
        a = sin(t += d = length(max( p = abs(mod(p, f)-f*.5)-cos(t)*.04-.06 , 0.)) 
                 + min(max(p, max(p, p.z).y).x, 0.));
}

