void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // We don't do anything special here
    // Simply draw Buffer A
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

#define extra_frame_delay 120.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // get the screen UV and the vertical 'slit' position
    vec2 uv = fragCoord/iResolution.xy;
    float slitpos = mod(float(iFrame), iResolution.y + extra_frame_delay);

    // sample the previous frame
    vec3 last = texture(iChannel0, uv).rgb;
    vec3 col = last;

    // draw the 1-pixel thin new slit
    float slit = (step(slitpos - fragCoord.y, 1.0));
    col = mix(col, texture(iChannel1, uv).rgb, slit);

    // draw the red bar above the slit
    float bar = (step(slitpos - fragCoord.y, 0.0) * step(-9.0, slitpos - fragCoord.y));
    col = mix(col, vec3(1.0, 0.0, 0.0), bar);

    fragColor = vec4(col, 1.0);

}
