#define t iTime/4.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.*(fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);
    float ti = 6.32/4. + sin(t)/10.;
    float x = uv.x;
    float y = uv.y;
    float m,accum;
    for(int i =0;i<15;i++) {
    m = abs(x)*abs(x)+abs(y)*abs(y)+0.0001;
    x = abs(x)/m-0.5*tan(sin(ti));
    y = abs(y)/m-0.5*tan(sin(ti)*mod(sin(ti)*.5,5.));
    if (m>sin(t*5.)*55.){accum =1.;break;}
    accum += m * float(exp(float(1) / 100.)) /100.;
    }    
    fragColor = vec4(vec3(accum),.1);
}
